  }
}

let femaleNames = global.get("femaleNames") || [];
let maleNames = global.get("maleNames") || [];
let lastNames = global.get("lastNames") || [];

function createVisitor(designatedId){
  let newVisitor = {};
  let gender = pickRandom(['male', 'female'])
  let nameVisitor=''
  if (gender == 'female'){
    nameVisitor = pickRandom(femaleNames)
  }else{
    nameVisitor = pickRandom(maleNames)
  }
  newVisitor = {
    "id": designatedId,
    "role": generateRole(),
    "gender":gender,
    "name": nameVisitor,
    "lastname": pickRandom(lastNames),
    "consent": pickRandom([true, false])
  }
  return newVisitor
}

function generateVisitor() {
  let numVisitors = global.get("visitors")
  let exhibition_picked = pickRandom(exhibitions_options)
  global.set("count" + exhibition_picked, global.get("count" + exhibition_picked) + 1)

  let listKey = "list" + exhibition_picked;
  let tempList = global.get(listKey) || { "visitors": [] };
  let newVisitor = createVisitor(numVisitors + 1)
  tempList.visitors.push(newVisitor);

  global.set("visitors", numVisitors +1);
  if (newVisitor.consent){
    global.set("visitorsConsent", global.get("visitorsConsent")+1);
  }else{
    global.set("visitorsNotConsent", global.get("visitorsNotConsent")+1);
  }
  return newVisitor
}

function deleteVisitor() {
    // Pick a random exhibition
    let exhibition_picked = pickRandom(exhibitions_options);

    // Get the list of visitors for the picked exhibition
    let listKey = "list" + exhibition_picked;
    let tempList = global.get(listKey) || { "visitors": [] };

    // Ensure there are visitors to remove
    if (tempList.visitors.length > 0 && global.get("count" + exhibition_picked) > 0) {
        // Randomly pick a visitor ID from the specific exhibition's visitor list
        let randomIndex = Math.floor(Math.random() * tempList.visitors.length);
        let idToRemove = tempList.visitors[randomIndex].id;

        // Update the exhibition-specific count
        global.set("count" + exhibition_picked, global.get("count" + exhibition_picked) - 1);

        // Remove the visitor from the specific exhibition list
        tempList.visitors = removeVisitor(tempList.visitors, idToRemove);

        // Update the global list and visitor count
        global.set(listKey, tempList);
        global.set("visitors", (global.get("visitors") || 0) - 1);

        return {
            idToRemove: idToRemove,
            exhibition_picked: exhibition_picked,
            num_visitors: global.get("visitors"),
        };
    }

    // If no visitors to remove
    return {
        idToRemove: null,
        exhibition_picked: exhibition_picked,
        num_visitors: global.get("visitors"),
    };
}


function removeVisitor(visitorList, visitorId) {
    // Get the visitor to be removed
    const visitorToRemove = visitorList.find(visitor => visitor.id === visitorId);

    if (visitorToRemove) {
        // Check the consent property and update global variables
        if (visitorToRemove.consent === true) {
          global.set("visitorsConsent", (global.get("visitorsConsent") - 1));
        } else if (visitorToRemove.consent === false) {
          global.set("visitorsNotConsent", (global.get("visitorsNotConsent") - 1));
        }
    }

    // Return the updated list without the removed visitor
    return visitorList.filter(visitor => visitor.id !== visitorId);
}

